from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

from p_turn import p_turn_incremental_sync

# Set up all devices.
prime = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
right_light_sensor = ColorSensor(Port.A)
left_light_sensor = ColorSensor(Port.B)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 110)
left_attachment = Motor(Port.D, Direction.CLOCKWISE)
right_attachment = Motor(Port.C, Direction.COUNTERCLOCKWISE)

def p_turn_increment(angle):
    p_turn_incremental_sync(angle, left, right, prime, 26)

def bumblebee_run():
    prime.imu.reset_heading(0)
    drive_base.use_gyro(True)
    drive_base.settings(straight_speed=500)
    drive_base.settings(straight_acceleration=750)
    drive_base.straight(670)
    p_turn_increment(45)
    while not left_light_sensor.reflection() < 20:
        drive_base.drive(100, 0)
    drive_base.straight(0)
    left_attachment.run_angle(500, 100)
    drive_base.straight(60)
    right_attachment.run_angle(200, 190)
    wait(250)
    right_attachment.run_angle(190, -190)
    drive_base.straight(-110)
    drive_base.settings(straight_speed=155)
    drive_base.arc(55, angle=105)
    drive_base.settings(straight_speed=150)
    drive_base.straight(-90)
    drive_base.straight(110)


# The main program starts here.
bumblebee_run()
