from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

from p_turn import p_turn_incremental_async

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left_light_sensor = ColorSensor(Port.B)
Right_light_sensor = ColorSensor(Port.A)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 110)
left_attachment_motor = Motor(Port.D, Direction.CLOCKWISE)
right_attachment_motor = Motor(Port.C, Direction.CLOCKWISE)

# Initialize variables.
heading_before_turn = 0
statue_heading_stop = 0
incremental_return_to_original = 0

async def p_turn_increment(angle):
    await wait(0)
    await p_turn_incremental_async(angle, left, right, prime_hub, 27)

async def subtask():
    global statue_heading_stop
    await p_turn_increment(22)
    statue_heading_stop = prime_hub.imu.heading()

async def subtask2():
    global statue_heading_stop
    await wait(1000)
    statue_heading_stop = prime_hub.imu.heading()

async def sandstorm_run():
    global heading_before_turn, statue_heading_stop, incremental_return_to_original
    await wait(0)
    drive_base.use_gyro(True)
    drive_base.settings(straight_speed=250)
    await multitask(
        drive_base.straight(-365),
        right_attachment_motor.run_angle(1000, -185),
    )
    await wait(25)
    drive_base.settings(straight_speed=250)
    await drive_base.straight(160)
    await p_turn_increment(-49.5)
    await drive_base.straight(-196)
    await p_turn_increment(45)
    await drive_base.straight(-130)
    await left_attachment_motor.run_angle(180, 180)
    await wait(25)
    await left_attachment_motor.run_angle(750, -180)
    await drive_base.straight(100)
    await p_turn_increment(-15)
    await drive_base.straight(-300)
    await p_turn_increment(15)
    while not await Right_light_sensor.reflection() < 20:
        await wait(0)
        drive_base.drive(-250, 0)
    await p_turn_increment(50)
    right_attachment_motor.reset_angle(0)
    await right_attachment_motor.run_angle(1000, 185)
    await drive_base.straight(210)
    await multitask(
        p_turn_increment(15),
        left_attachment_motor.run_angle(1000, 180),
    )
    await right_attachment_motor.run_angle(1000, -187)
    heading_before_turn = prime_hub.imu.heading()
    await multitask(
        subtask(),
        subtask2(),
        race=True,
    )
    incremental_return_to_original = -1 * abs(statue_heading_stop - heading_before_turn)
    await p_turn_increment(incremental_return_to_original)
    await left_attachment_motor.run_angle(1000, -180)
    await right_attachment_motor.run_angle(1000, -275)
    await p_turn_increment(95)
    await drive_base.straight(210)
    await p_turn_increment(-35)
    await drive_base.straight(-175)
    await p_turn_increment(-80)
    drive_base.settings(straight_speed=1000)
    await drive_base.straight(-900)

async def main():
    await sandstorm_run()


run_task(main())