from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

from p_turn import p_turn_incremental_sync

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 56, 114)
right_attachment = Motor(Port.C, Direction.CLOCKWISE)
left_attachment = Motor(Port.D, Direction.COUNTERCLOCKWISE)

def p_turn_increment_garden(angle):
    p_turn_incremental_sync(angle, left, right, prime_hub)

def gardener_run():
    drive_base.settings(straight_speed=500)
    drive_base.settings(straight_acceleration=750)
    drive_base.straight(500)
    p_turn_increment_garden(30)
    drive_base.settings(straight_speed=400)
    drive_base.straight(154)
    p_turn_increment_garden(-75)
    drive_base.settings(straight_speed=250)
    drive_base.straight(220)
    drive_base.straight(-50)
    left_attachment.run_angle(500, 270)
    drive_base.straight(50)
    left_attachment.run_angle(500, -275)
    drive_base.settings(straight_speed=900)
    drive_base.settings(straight_acceleration=800)
    drive_base.straight(-110)
    p_turn_increment_garden(50)
    drive_base.straight(-600)


# The main program starts here.
gardener_run()
