from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

from p_turn import p_turn_incremental_sync

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 110)
right_attachment = Motor(Port.C, Direction.CLOCKWISE)
left_attachment = Motor(Port.D, Direction.COUNTERCLOCKWISE)

def p_turn_increment_garden(angle):
    p_turn_incremental_sync(angle, left, right, prime_hub, 26)

def gardener_run():
    drive_base.settings(straight_speed=350)
    drive_base.straight(520)
    p_turn_increment_garden(30)
    drive_base.straight(135)
    p_turn_increment_garden(-75)
    drive_base.straight(205)
    drive_base.straight(-50)
    left_attachment.run_angle(500, 200)
    drive_base.straight(50)
    left_attachment.run_angle(500, -200)
    drive_base.straight(-120)
    p_turn_increment_garden(45)
    drive_base.straight(-140)
    p_turn_increment_garden(90)
    drive_base.straight(-80)
    right_attachment.run_angle(1500, -1080)
    right_attachment.run_angle(1500, 1080)
    drive_base.straight(90)
    p_turn_increment_garden(-60)
    drive_base.settings(straight_speed=520)
    drive_base.straight(-520)


# The main program starts here.
gardener_run()
