from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

from p_turn import p_turn_incremental_sync

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 56, 114)
right_attachment = Motor(Port.C, Direction.CLOCKWISE)
Left_attatchment = Motor(Port.D, Direction.COUNTERCLOCKWISE)

def p_turn_increment(angle):
    p_turn_incremental_sync(angle, left, right, prime_hub, 18)


# The main program starts here.
drive_base.settings(straight_speed=350)
drive_base.straight(680)
p_turn_increment(-45)
drive_base.straight(100)
drive_base.straight(-50)
Left_attatchment.run_angle(500, 270)
drive_base.straight(50)
Left_attatchment.run_angle(500, -275)
drive_base.straight(-110)
p_turn_increment(50)
drive_base.settings(straight_speed=750)
drive_base.straight(-600)
