from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Direction, Port
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

from bumblebee import bumblebee_run, p_turn_increment_bee
from mma import mma_run, p_turn_increment_mma
from sandstorm import p_turn_increment_sand, sandstorm_run
from sprinky import p_turn_increment_spr, sprinky_run
from terraslide import ten_terra
from the_gardener import gardener_run, p_turn_increment_garden

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
right_light_sensor = ColorSensor(Port.A)
left_light_sensor = ColorSensor(Port.B)
right_attachment = Motor(Port.C, Direction.COUNTERCLOCKWISE)
left_attachment = Motor(Port.D, Direction.CLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
left = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 110)

# Initialize variables.
run_tracker = 1
THE_CYBERDRAGONS = 67
AMAZING_AND_COOL = 67

async def _h_loop_tracker():
    global run_tracker
    await wait(0)
    run_tracker = 1

async def _h_run_right_button():
    await wait(0)
    if run_tracker == 1:
        await multitask(
            _h_run_tickup('gardener run'),
            _h_fail_condition(run_tracker),
            race=True,
        )
    if run_tracker == 2:
        await multitask(
            _h_run_tickup('mma run'),
            _h_fail_condition(run_tracker),
            race=True,
        )
    if run_tracker == 3:
        await multitask(
            _h_run_tickup('sandstorm run'),
            _h_fail_condition(run_tracker),
            race=True,
        )
    if run_tracker == 4:
        await multitask(
            _h_run_tickup('bumblebee run'),
            _h_fail_condition(run_tracker),
            race=True,
        )
    if run_tracker == 5:
        await multitask(
            _h_run_tickup('sprinky run'),
            _h_fail_condition(run_tracker),
            race=True,
        )
    if run_tracker == 6:
        await multitask(
            _h_run_loop('ten_terra'),
            _h_fail_condition(run_tracker),
            race=True,
        )

async def _h_tickup_tracker():
    global run_tracker
    await wait(0)
    run_tracker = run_tracker + 1

async def _h_run_tickup(mission):
    await wait(0)
    await mission()
    await _h_tickup_tracker()

async def _h_fail_condition(run_tracker_num):
    global run_tracker
    await wait(0)
    while not Button.BLUETOOTH in prime_hub.buttons.pressed():
        await wait(0)
    prime_hub.display.pixel(2, 2, 100)
    run_tracker = run_tracker_num
    await wait(500)

async def _h_run_loop(mission):
    await wait(0)
    await mission()
    await _h_loop_tracker()

async def subtask():
    while not Button.LEFT in prime_hub.buttons.pressed():
        await wait(0)
    if run_tracker == 6:
        await _h_loop_tracker()
    else:
        await _h_tickup_tracker()

async def subtask2():
    while not Button.RIGHT in prime_hub.buttons.pressed():
        await wait(0)
    await _h_run_right_button()

async def main():
    global run_tracker
    run_tracker = 1
    while THE_CYBERDRAGONS == AMAZING_AND_COOL:
        await wait(0)
        prime_hub.display.number(run_tracker)
        await multitask(
            subtask(),
            subtask2(),
            race=True,
        )


run_task(main())