from pybricks.tools import multitask, run_task, wait

async def sleigh_ride_run():
    await wait(0)
    drive_base.settings(straight_speed=400)
    await drive_base.straight(270)
    await p_turn_increment_sleigh(-30)
    await drive_base.straight(375)
    await p_turn_increment_sleigh(-23)
    await drive_base.straight(373)
    await drive_base.straight(-150)
    await p_turn_increment_sleigh(-30)
    await drive_base.straight(200)
    await p_turn_increment_sleigh(45)
